create table if not exists curriculum_subjects (
  id int primary key,
  code text not null unique,
  name text not null,
  area text not null
);

create table if not exists curriculum_units (
  id int primary key,
  subject_id int not null references curriculum_subjects(id) on delete cascade,
  grade text not null, -- 1EM, 2EM, 3EM
  title text not null
);

create table if not exists curriculum_topics (
  id int primary key,
  unit_id int not null references curriculum_units(id) on delete cascade,
  order_index int not null,
  title text not null,
  default_lessons int not null default 1
);

create index if not exists idx_curr_units_subject_grade on curriculum_units(subject_id, grade);
create index if not exists idx_curr_topics_unit_order on curriculum_topics(unit_id, order_index);

